CREATE TABLE kunden (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment,nummer VARCHAR(250),Firma VARCHAR(250) default NULL,Anrede VARCHAR(250) default NULL,Vorname VARCHAR(250) default NULL, Name VARCHAR(250) default NULL, Str VARCHAR(250) default NULL, PLZ VARCHAR(50) default NULL,Ort VARCHAR(300) default NULL, Tel VARCHAR(250) default NULL,Fax VARCHAR(250) default NULL,Mobil VARCHAR(250) default NULL,Mail VARCHAR(350) default NULL, Webseite VARCHAR(350) default NULL,Notizen VARCHAR(10000),Steuernummer VARCHAR(350) default NULL,Datum TIMESTAMP DEFAULT CURRENT_TIMESTAMP,deleted INTEGER DEFAULT 0, reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE lieferanten (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment,nummer VARCHAR(250),Firma VARCHAR(250) default NULL,Anrede VARCHAR(250) default NULL,Vorname VARCHAR(250) default NULL, Name VARCHAR(250) default NULL, Str VARCHAR(250) default NULL, PLZ VARCHAR(50) default NULL,Ort VARCHAR(300) default NULL, Tel VARCHAR(250) default NULL,Fax VARCHAR(250) default NULL,Mobil VARCHAR(250) default NULL,Mail VARCHAR(350) default NULL, Webseite VARCHAR(350) default NULL,Notizen VARCHAR(10000),Datum TIMESTAMP DEFAULT CURRENT_TIMESTAMP,deleted INTEGER DEFAULT 0, reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE hersteller (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment,nummer VARCHAR(250),Firma VARCHAR(250) default NULL,Anrede VARCHAR(250) default NULL,Vorname VARCHAR(250) default NULL, Name VARCHAR(250) default NULL, Str VARCHAR(250) default NULL, PLZ VARCHAR(50) default NULL,Ort VARCHAR(300) default NULL, Tel VARCHAR(250) default NULL,Fax VARCHAR(250) default NULL,Mobil VARCHAR(250) default NULL,Mail VARCHAR(350) default NULL, Webseite VARCHAR(350) default NULL,Notizen VARCHAR(10000),Datum TIMESTAMP DEFAULT CURRENT_TIMESTAMP,deleted INTEGER DEFAULT 0, reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE rechnungen (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment,Rechnungnummer VARCHAR(250) UNIQUE NOT NULL, KundenID BIGINT REFERENCES kunden (id) ON DELETE CASCADE,Datum DATE NOT NULL,storno INTEGER DEFAULT 0,bezahlt INTEGER DEFAULT 0,gesamtpreis DOUBLE DEFAULT 0,gesamttax DOUBLE DEFAULT 0,AfDatum DATE NOT NULL,mahnungen INTEGER DEFAULT 0,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE rechnungsposten (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, rechnungID BIGINT REFERENCES rechnungen (id) ON DELETE CASCADE,anzahl DOUBLE DEFAULT 0 NOT NULL,posten VARCHAR(1000) default NULL, preis DOUBLE DEFAULT 0 NOT NULL, steuersatz DOUBLE DEFAULT 0 NOT NULL,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE angebote (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment,angebotnummer VARCHAR(250) default NULL, KundenID BIGINT REFERENCES kunden(id) ON DELETE CASCADE,Datum DATE NOT NULL,auftragdatum DATE DEFAULT NULL,anfragevom DATE NOT NULL,validvon DATE NOT NULL,validbis DATE NOT NULL,rechnungID BIGINT DEFAULT 0,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE angebotposten (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, angebotID BIGINT REFERENCES angebote(id) ON DELETE CASCADE,anzahl DOUBLE DEFAULT 0 NOT NULL,posten VARCHAR(1000) default NULL, preis DOUBLE DEFAULT 0 NOT NULL, steuersatz DOUBLE DEFAULT 0 NOT NULL,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE einstellungen (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, name VARCHAR(250), wert VARCHAR(2500),deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE rechnungbetreffz (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, name VARCHAR(100), text VARCHAR(500), isvorlage INTEGER DEFAULT 0,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE programmdaten (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, name VARCHAR(250), wert VARCHAR(2500),deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE steuersaetze (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, name VARCHAR(250), wert DOUBLE DEFAULT 0,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE dienstleistungen (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, produktnummer VARCHAR(500),name VARCHAR(50),beschreibung VARCHAR(500),einheit VARCHAR(50),preis DOUBLE DEFAULT 0, steuersatzID BIGINT REFERENCES steuersaetze (id),warengruppenID BIGINT DEFAULT 0,datum DATE NOT NULL,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE produkte (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, produktnummer VARCHAR(500),name VARCHAR(150),text VARCHAR(500),vk DOUBLE DEFAULT 0,ek DOUBLE DEFAULT 0, steuersatzID BIGINT REFERENCES steuersaetze (id),herstellerID BIGINT REFERENCES hersteller(id) ON DELETE CASCADE, lieferantenID BIGINT REFERENCES lieferanten (id) ON DELETE CASCADE, warengruppenID BIGINT DEFAULT 0,datum DATE NOT NULL,url VARCHAR(250) default NULL,ean VARCHAR(25),bestellnr VARCHAR(50),herstellernr VARCHAR(50),lieferantennr VARCHAR(50),bestelldatum VARCHAR(50),bestellmenge DOUBLE DEFAULT 0, lagermenge DOUBLE DEFAULT 0,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE warengruppenkategorien (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, kategorienummer VARCHAR(120),name VARCHAR(500),deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE warengruppenfamilien (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, familienummer VARCHAR(120), kategorieid BIGINT REFERENCES warengruppenkategorien(id) ON DELETE CASCADE, name VARCHAR(500),deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE warengruppengruppen (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, gruppenummer VARCHAR(120),familienid BIGINT REFERENCES warengruppenfamilien(id) ON DELETE CASCADE, name VARCHAR(500),deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE benutzer (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, username VARCHAR(250),password VARCHAR(250),createdon DATE default NULL,createdby VARCHAR(50),iseditor INTEGER DEFAULT 0,isadmin INTEGER DEFAULT 0,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE dateien (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, productID BIGINT DEFAULT 0,url VARCHAR(500),datum DATE NOT NULL,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE historie (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, aktion VARCHAR(500) default NULL,text VARCHAR(250) default NULL,datum DATE NOT NULL,benutzer VARCHAR(500) default NULL,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE konten (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, nummer VARCHAR(500) default NULL,klasse VARCHAR(1000) default NULL,gruppe VARCHAR(1000) default NULL,art VARCHAR(1000) default NULL,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE ausgaben (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, kontenID BIGINT REFERENCES konten (id), beschreibung VARCHAR(500) default NULL,preis DOUBLE DEFAULT 0,tax INTEGER NOT NULL,datum DATE NOT NULL,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE einnahmen (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, kontenID BIGINT REFERENCES konten (id), beschreibung VARCHAR(500) default NULL,preis DOUBLE DEFAULT 0,tax INTEGER NOT NULL,datum DATE NOT NULL,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE betreffz_zu_rechnung (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, rechnungID BIGINT REFERENCES rechnungen (id) ON DELETE CASCADE,betreffzID BIGINT REFERENCES rechnungbetreffz (id) ON DELETE CASCADE,deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE plugins_to_load (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment, name VARCHAR(250), wert VARCHAR(2500),deleted INTEGER DEFAULT 0,reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL)
CREATE TABLE tablelock (ID BIGINT(20) UNSIGNED NOT NULL PRIMARY KEY auto_increment,tablename VARCHAR(250), rowID BIGINT NOT NULL, userID BIGINT REFERENCES benutzer (id) ON DELETE CASCADE,deleted INTEGER DEFAULT 0, reserve1 VARCHAR(500) default NULL,reserve2 VARCHAR(500) default NULL) 